import React, { useState, useEffect } from 'react';
import axios from 'axios';
import './App.css';

const Pricefilter = () => {
  const [posts, setPosts] = useState([]);  // Initialize with an empty array
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState(null);
  const [priceFilter, setPriceFilter] = useState(''); // State for selected price filter

  useEffect(() => {
    const fetchPosts = async () => {
      try {
        const response = await axios.get('http://127.0.0.1:8000/posts', {
          params: {
            price_filter: 'lac'
          }
        });
        setPosts(response.data.posts);  // Ensure this matches the key in your JSON response
        setLoading(false);
      } catch (error) {
        setError(error);
        setLoading(false);
      }
    };

    fetchPosts();
  }, [priceFilter]);  // Add priceFilter to dependency array to refetch data when it changes

  const handleFilterChange = (filter) => {
    setLoading(true);
    setPriceFilter(filter);  // Update priceFilter state to trigger useEffect and refetch data
  };

  if (loading) return <div>Loading...</div>;
  if (error) return <div>Error: {error.message}</div>;

  return (
    <div>
      <div>
        <button onClick={() => handleFilterChange('lac')}>Filter by Lac</button>
        {/* <button onClick={() => handleFilterChange('cr')}>Filter by Crore</button> */}
      </div>
      <div>
        {posts.map(post => (
          <ul key={post.property_id}>
            {/* <h3>{post.name}</h3>
            <img src={post.Image_url} alt={post.name} className='image'/> */}
            <h4>{post.price}</h4>
            {/* <h4>{post.beds}</h4>
            <h4>{post.super_areas}</h4>
            <h4>{post.furnishing}</h4> */}
          </ul>
        ))}
      </div>
    </div>
  );
};

export default Pricefilter;
