import React, { useState, useEffect } from 'react';
import axios from 'axios';
import './App.css';

const Pricefilter = () => {
  const [allPosts, setAllPosts] = useState([]);  // Store all posts fetched from the backend
  const [filteredPosts, setFilteredPosts] = useState([]);  // Store the filtered posts to be displayed
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState(null);
  const [priceFilter, setPriceFilter] = useState('lac'); // State for selected price filter

  useEffect(() => {
    const fetchPosts = async () => {
      try {
        const response = await axios.get('http://127.0.0.1:8000/api/posts/');
        setAllPosts(response.data.posts);  // Ensure this matches the key in your JSON response
        setLoading(false);
      } catch (error) {
        setError(error);
        setLoading(false);
      }
    };

    fetchPosts();
  }, []);

  useEffect(() => {
    const filterPosts = () => {
      let filtered;
      if (priceFilter === 'lac') {
        filtered = allPosts.filter(post => post.price.includes('Lac'));
      } else if (priceFilter === 'cr') {
        filtered = allPosts.filter(post => post.price.includes('Cr'));
      } else {
        filtered = allPosts;
      }
      setFilteredPosts(filtered);
    };

    filterPosts();
  }, [allPosts, priceFilter]);

  const handleFilterChange = (filter) => {
    setPriceFilter(filter);  // Update priceFilter state to trigger useEffect and refilter data
  };

  if (loading) return <div>Loading...</div>;
  if (error) return <div>Error: {error.message}</div>;

  return (
    <div>
      <div>
        <button onClick={() => handleFilterChange('lac')}>Filter by Lac</button>
        <button onClick={() => handleFilterChange('cr')}>Filter by Crore</button>
      </div>
      <div>
        {filteredPosts.map(post => (
          <ul key={post.property_id}>
            <h3>{post.name}</h3>
            <img src={post.Image_url} alt={post.name} className='image'/>
            <h4>{post.price}</h4>
            <h4>{post.beds}</h4>
            <h4>{post.super_areas}</h4>
            <h4>{post.furnishing}</h4>
          </ul>
        ))}
      </div>
    </div>
  );
};

export default Pricefilter;
